pragma solidity >=0.8.0;

import "../../stableswap/interfaces/IStableSwap.sol";

interface ISwapRouterV1 {

    struct Route {
        bool stable;
        bytes callData; 
    }

    function factory() external view returns (address);

    function WNativeCurrency() external view returns (address);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactNativeCurrencyForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactNativeCurrency(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForNativeCurrency(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapNativeCurrencyForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapPool(
        IStableSwap pool,
        uint8 fromIndex,
        uint8 toIndex,
        uint256 inAmount,
        uint256 minOutAmount,
        address to,
        uint256 deadline
    ) external returns (uint256 amountOut);

    function swapPoolFromBase(
        IStableSwap pool,
        IStableSwap basePool,
        uint8 tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256 dx,
        uint256 minDy,
        address to,
        uint256 deadline
    ) external returns (uint256 amountOut);

    function swapPoolToBase(
        IStableSwap pool,
        IStableSwap basePool,
        uint8 tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256 dx,
        uint256 minDy,
        address to,
        uint256 deadline
    ) external returns (uint256 amountOut);

    function swapExactTokensForTokensThroughStablePool(
        uint256 amountIn,
        uint256 amountOutMin,
        Route[] calldata routes,
        address to,
        uint256 deadline
    ) external returns (uint256 amountOut);

    function swapExactNativeCurrencyForTokensThroughStablePool(
        uint256 amountOutMin,
        Route[] calldata routes,
        address to,
        uint256 deadline
    ) external payable returns (uint256 amountOut);

    function swapExactTokensForNativeCurrencyThroughStablePool(
        uint256 amountIn,
        uint256 amountOutMin,
        Route[] calldata routes,
        address to,
        uint256 deadline
    ) external returns (uint256 amountOut);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(
        uint256 amountIn, 
        address[] memory path
    ) external view returns (uint256[] memory amounts);

    function getAmountsIn(
        uint256 amountOut, 
        address[] memory path
    ) external view returns (uint256[] memory amounts);

    function calculateSwap(
        IStableSwap pool,
        uint8 fromIndex,
        uint8 toIndex,
        uint256 inAmount
    ) external view returns (uint256);

    function calculateSwapFromBase(
        IStableSwap pool,
        IStableSwap basePool,
        uint8 tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256 dx
    ) external view returns (uint256);

    function calculateSwapToBase(
        IStableSwap pool,
        IStableSwap basePool,
        uint8 tokenIndexFrom,
        uint8 tokenIndexTo,
        uint256 dx
    ) external view returns (uint256);
}

contracts/libraries/Helper.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.0;

import "./Math.sol";
import "../core/interfaces/IPair.sol";
import "../core/interfaces/IFactory.sol";

library Helper {
    using Math for uint256;

    function sortTokens(address tokenA, address tokenB)
        internal
        pure
        returns (address token0, address token1)
    {
        require(tokenA != tokenB, "Helper: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        require(token0 != address(0), "Helper: ZERO_ADDRESS");
    }

    function pairFor(
        address factory,
        address tokenA,
        address tokenB
    ) internal view returns (address pair) {
        return IFactory(factory).getPair(tokenA, tokenB);
    }

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (uint256 amountB) {
        require(amountA > 0, "INSUFFICIENT_AMOUNT");
        require(reserveA > 0 && reserveB > 0, "INSUFFICIENT_LIQUIDITY");
        amountB = amountA.mul(reserveB) / reserveA;
    }

    function getReserves(
        address factory,
        address tokenA,
        address tokenB
    ) internal view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IPair(
            pairFor(factory, tokenA, tokenB)
        ).getReserves();
        (reserveA, reserveB) = tokenA == token0
            ? (reserve0, reserve1)
            : (reserve1, reserve0);
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x23b872dd, from, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::transferFrom: transferFrom failed"
        );
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0xa9059cbb, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::safeTransfer: transfer failed"
        );
    }

    function safeTransferNativeCurrency(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(
            success,
            "TransferHelper::safeTransferNativeCurrency: NativeCurrency transfer failed"
        );
    }

    // given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountOut) {
        require(amountIn > 0, "Helper: INSUFFICIENT_INPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "Helper: INSUFFICIENT_LIQUIDITY"
        );
        uint256 amountInWithFee = amountIn.mul(997);
        uint256 numerator = amountInWithFee.mul(reserveOut);
        uint256 denominator = reserveIn.mul(1000).add(amountInWithFee);
        amountOut = numerator / denominator;
    }

    // given an output amount of an asset and pair reserves, returns a required input amount of the other asset
    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountIn) {
        require(amountOut > 0, "Helper: INSUFFICIENT_OUTPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "Helper: INSUFFICIENT_LIQUIDITY"
        );
        uint256 numerator = reserveIn.mul(amountOut).mul(1000);
        uint256 denominator = reserveOut.sub(amountOut).mul(997);
        amountIn = (numerator / denominator).add(1);
    }

    // performs chained getAmountOut calculations on any number of pairs
    function getAmountsOut(
        address factory,
        uint256 amountIn,
        address[] memory path
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "Helper: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;
        for (uint256 i; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i],
                path[i + 1]
            );
            amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
        }
    }

    function getAmountsIn(
        address factory,
        uint256 amountOut,
        address[] memory path
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "Helper: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[amounts.length - 1] = amountOut;
        for (uint256 i = path.length - 1; i > 0; i--) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i - 1],
                path[i]
            );
            amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut);
        }
    }
}

contracts/periphery/Stake.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "../libraries/Helper.sol";
import "../libraries/Math.sol";
import "../libraries/AdminUpgradeable.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract Stake is ReentrancyGuard, AdminUpgradeable {
    using Math for uint256;

    // Info of each staker
    struct StakerInfo {
        uint256 stakedAmount;      // How many stake tokens the user has provided
        uint256 lastUpdatedBlock;  // Last block number that user behavior occurs
        uint256 accInterest;       // Accumulated interest the user has owned
    }

    // The STAKED TOKEN
    address public immutable STAKED_TOKEN;
    // The REWARD TOKEN
    address public immutable REWARD_TOKEN;
    // The block when stake starts
    uint256 public immutable START_BLOCK;
    // The block when stake ends
    uint256 public immutable END_BLOCK;
    // The total interest of whole stake
    uint256 public totalInterest;
    // The total staked amount of whole stake
    uint256 public totalStakedAmount;
    // The total reward amount of whole stake
    uint256 public totalRewardAmount;

    // Is stake paused
    bool private _stakePaused;

    // Info of each staker that stakes token
    mapping(address => StakerInfo) private _stakerInfos;

    event Staked(address indexed user, uint256 amount, uint256 interest);
    event Redeem(address indexed user, uint256 redeemAmount, uint256 interest);
    event RewardsClaimed(address indexed to, uint256 amount);
    event WithdrawExtraFunds(address indexed token, address indexed to, uint256 amount);
    event StakePaused(address indexed caller);
    event StakeUnpaused(address indexed caller);

    constructor(
        address _stakeToken,
        address _rewardToken,
        uint256 _startBlock,
        uint256 _endBlock
    ) {
        require(_startBlock >= block.number, 'INVALID_START_BLOCK');
        require(_endBlock > _startBlock, 'INVALID_STAKE_PERIOD');

        _initializeAdmin(msg.sender);
        STAKED_TOKEN = _stakeToken;
        REWARD_TOKEN = _rewardToken;
        START_BLOCK = _startBlock;
        END_BLOCK = _endBlock;
        totalRewardAmount = IERC20(_rewardToken).balanceOf(address(this));

        _stakePaused = false;
    }

    modifier beforeEndPeriod() {
        require(block.number < END_BLOCK, "OVER_PERIOD");
        _;
    }

    modifier whenStakeNotPaused() {
        require(!_stakePaused, "STAKE_PAUSED");
        _;
    }

    /**
     * @dev add reward amount by admin
     **/
    function addReward(uint256 amount) external onlyAdmin beforeEndPeriod {
        Helper.safeTransferFrom(
            REWARD_TOKEN,
            msg.sender,
            address(this),
            amount
        );
        totalRewardAmount = totalRewardAmount.add(amount);
    }

    /**
     * @dev remove reward amount by admin
     **/
    function removeReward(uint256 amount) external onlyAdmin beforeEndPeriod {
        require(amount <= totalRewardAmount, 'INSUFFICIENT_REWARD_AMOUNT');
        Helper.safeTransfer(REWARD_TOKEN, msg.sender, amount);
        totalRewardAmount = totalRewardAmount.sub(amount);
    }

    /**
     * @dev Return funds directly transfered to this contract, will not affect the portion of the amount 
     *      that participated in stake using `stake` function
     **/
    function withdrawExtraFunds(address token, address to, uint256 amount) external onlyAdmin {
        if (token == STAKED_TOKEN) {
            uint256 stakedBalance = IERC20(STAKED_TOKEN).balanceOf(address(this));
            require(stakedBalance.sub(amount) >= totalStakedAmount, 'INSUFFICIENT_STAKED_BALANCE');
        }
        if (token == REWARD_TOKEN) {
            uint256 rewardBalance = IERC20(REWARD_TOKEN).balanceOf(address(this));
            require(rewardBalance.sub(amount) >= totalRewardAmount, 'INSUFFICIENT_REWARD_BALANCE');
        }
        Helper.safeTransfer(token, to, amount);

        emit WithdrawExtraFunds(token, to, amount);
    }

    function getStakerInfo(address staker) 
        external 
        view 
        returns (uint256 stakedAmount, uint256 accInterest)  
    {
        StakerInfo memory stakerInfo = _stakerInfos[staker];
        stakedAmount = stakerInfo.stakedAmount;
        accInterest = stakerInfo.accInterest;
    }

    function pauseStake() external onlyAdmin {
        require(!_stakePaused, 'STAKE_PAUSED');
        _stakePaused = true;
        emit StakePaused(msg.sender);
    }

    function unpauseStake() external onlyAdmin {
        require(_stakePaused, 'STAKE_UNPAUSED');
        _stakePaused = false;
        emit StakeUnpaused(msg.sender);
    }

    /**
     * @dev Stakes tokens
     * @param amount Amount to stake
     **/
    function stake(uint256 amount) external beforeEndPeriod nonReentrant whenStakeNotPaused {
        require(amount > 0, 'INVALID_ZERO_AMOUNT');
        StakerInfo storage stakerInfo = _stakerInfos[msg.sender];

        Helper.safeTransferFrom(
            STAKED_TOKEN,
            msg.sender,
            address(this),
            amount
        );

        stakerInfo.lastUpdatedBlock = block.number < START_BLOCK
            ? START_BLOCK
            : block.number;

        uint256 addedInterest = amount.mul(END_BLOCK.sub(stakerInfo.lastUpdatedBlock));

        totalInterest = totalInterest.add(addedInterest);
        totalStakedAmount = totalStakedAmount.add(amount);

        stakerInfo.stakedAmount = stakerInfo.stakedAmount.add(amount);
        stakerInfo.accInterest = stakerInfo.accInterest.add(addedInterest);

        emit Staked(msg.sender, amount, addedInterest);
    }

    /**
     * @dev Redeems staked tokens
     * @param amount Amount to redeem
     **/
    function redeem(uint256 amount) external nonReentrant {
        require(amount > 0, 'INVALID_ZERO_AMOUNT');
        require(block.number > START_BLOCK, "STAKE_NOT_STARTED");

        StakerInfo storage stakerInfo = _stakerInfos[msg.sender];
        require(amount <= totalStakedAmount, 'INSUFFICIENT_TOTAL_STAKED_AMOUNT');
        require(amount <= stakerInfo.stakedAmount, 'INSUFFICIENT_STAKED_AMOUNT');

        stakerInfo.lastUpdatedBlock = block.number < END_BLOCK ? block.number : END_BLOCK;

        uint256 removedInterest = amount.mul(END_BLOCK.sub(stakerInfo.lastUpdatedBlock));

        totalInterest = totalInterest.sub(removedInterest);
        totalStakedAmount = totalStakedAmount.sub(amount);

        stakerInfo.stakedAmount = stakerInfo.stakedAmount.sub(amount);
        stakerInfo.accInterest = stakerInfo.accInterest.sub(removedInterest);

        Helper.safeTransfer(STAKED_TOKEN, msg.sender, amount);
        emit Redeem(msg.sender, amount, removedInterest);
    }

    /**
     * @dev Return the total amount of estimated rewards from an staker
     * @param staker The staker address
     * @return The rewards
     */
    function getEstimatedRewardsBalance(address staker) external view returns (uint256) {
        StakerInfo memory stakerInfo = _stakerInfos[staker];
        if (totalInterest != 0) {
            return totalRewardAmount.mul(stakerInfo.accInterest) / totalInterest;
        }
        return 0;
    }

    /**
     * @dev Claims all amount of `REWARD_TOKEN` calculated from staker interest
     **/
    function claim() external nonReentrant {
        require(block.number > END_BLOCK, "STAKE_NOT_FINISHED");
        require(totalInterest > 0, 'INVALID_ZERO_TOTAL_INTEREST');

        StakerInfo storage stakerInfo = _stakerInfos[msg.sender];
        require(stakerInfo.accInterest > 0, "INSUFFICIENT_ACCUMULATED_INTEREST");

        uint256 claimRewardAmount = totalRewardAmount.mul(stakerInfo.accInterest) / totalInterest;

        stakerInfo.accInterest = 0;
        stakerInfo.lastUpdatedBlock = block.number;

        Helper.safeTransfer(REWARD_TOKEN, msg.sender, claimRewardAmount);
        emit RewardsClaimed(msg.sender, claimRewardAmount);
    }
}

contracts/periphery/Bootstrap.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.0;

import "../libraries/Math.sol";
import "../libraries/Helper.sol";
import "../libraries/AdminUpgradeable.sol";
import "../core/interfaces/IFactory.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract Bootstrap is ReentrancyGuard, AdminUpgradeable {
    using Math for uint256;

    struct UserInfo {
        uint256 amount0;
        uint256 amount1;
    }

    address public factory;
    address public token0;
    address public token1;

    uint256 public constant MINIMUM_LIQUIDITY = 10**3;
    uint256 public MINUM_AMOUNT0;
    uint256 public MINUM_AMOUNT1;
    uint256 public HARD_CAP_AMOUNT0;
    uint256 public HARD_CAP_AMOUNT1;
    uint256 public END_BLOCK;

    uint256 public totalAmount0;
    uint256 public totalAmount1;

    address[] private rewardTokens;
    address[] private limitTokens;
    uint256[] private limitTokenAmounts;
    uint256[] private rewardTokenAmounts;

    mapping(address => UserInfo) private _userInfos;

    event Provided(address indexed user, uint256 amount0, uint256 amount1);
    event LiquidityClaimed(address indexed to, uint256 amount);
    event Refund(address indexed to, uint256 amount0, uint256 amount1);
    event WithdrawExtraFunds(address indexed token, address indexed to, uint256 amount);
    event MinumAmountUpdated(uint256 amount0, uint256 amount1);
    event HardCapAmountUpdated(uint256 amount0, uint256 amount1);
    event EndBlockUpdated(uint256 endBlock);
    event DistributeReward(address indexed provider, address[] rewardTokens, uint256[] rewardAmount);
    event ChargeReward(address indexed sender, address[] rewardTokens, uint256[] totalAmount);
    event SetRewardAndLimit(address[] rewards, address[] limits, uint256[] limitAmounts);

    constructor(
        address _factory,
        address _tokenA,
        address _tokenB,
        uint256 _minumAmountA,
        uint256 _minumAmountB,
        uint256 _hardCapAmountA,
        uint256 _hardCapAmountB,
        uint256 _endBlock
    ) {
        require(_endBlock > block.number, 'INVALID_END_BLOCK');
        require(_hardCapAmountA > _minumAmountA && _hardCapAmountB > _minumAmountB, 'INVALID_HARD_CAP_AMOUNT');
        (address _token0, address _token1) = Helper.sortTokens(_tokenA, _tokenB);
        require(
            IFactory(_factory).getPair(_token0, _token1) == address(0), 
            'PAIR_EXISTS'
        );
        require(
            IFactory(_factory).getBootstrap(_token0, _token1) != address(0), 
            'BOOTSTRAP_NOT_EXISTS'
        );
        factory = _factory;
        token0 = _token0;
        token1 = _token1;
        MINUM_AMOUNT0 = _token0 == _tokenA ? _minumAmountA : _minumAmountB;
        MINUM_AMOUNT1 = _token0 == _tokenA ? _minumAmountB : _minumAmountA;
        HARD_CAP_AMOUNT0 = _token0 == _tokenA ? _hardCapAmountA : _hardCapAmountB;
        HARD_CAP_AMOUNT1 =  _token0 == _tokenA ? _hardCapAmountB : _hardCapAmountA;
        END_BLOCK = _endBlock;
        _initializeAdmin(msg.sender);
    }

    modifier whenNotEnded() {
        require(block.number < END_BLOCK, 'BOOTSTRAP_ENDED');
        _;
    }

    modifier whenEndedAndCapped {
        require(
            block.number >= END_BLOCK &&
            totalAmount0 >= MINUM_AMOUNT0 &&
            totalAmount1 >= MINUM_AMOUNT1,
            'NOT_ENDED_AND_CAPPED'
        );
        _;
    }

    modifier whenEndedAndFailed {
        require(
            block.number >= END_BLOCK &&
            (totalAmount0 < MINUM_AMOUNT0 || totalAmount1 < MINUM_AMOUNT1),
            'NOT_ENDED_AND_FAILED'
        );
        _;
    }

    modifier whenLiquidityMinted {
        address pair = Helper.pairFor(factory, token0, token1);
        require(pair != address(0), 'PAIR_NOT_CREATED');
        require(
            IERC20(pair).balanceOf(address(this)) > 0, 
            'LIQUIDITY_NOT_MINTED'
        );
        _;
    }

    function setMinumAmount0(uint256 amount0) 
        external 
        whenNotEnded 
        onlyAdmin 
    {
        MINUM_AMOUNT0 = amount0;
        emit MinumAmountUpdated(amount0, MINUM_AMOUNT1);
    }

    function setMinumAmount1(uint256 amount1) 
        external 
        whenNotEnded 
        onlyAdmin 
    {
        MINUM_AMOUNT1 = amount1;
        emit MinumAmountUpdated(MINUM_AMOUNT0, amount1);
    }

    function setHardCapAmount0(uint256 amount0)
        external
        whenNotEnded
        onlyAdmin
    {
        require(amount0 > MINUM_AMOUNT0, 'INVALID_AMOUNT0');
        HARD_CAP_AMOUNT0 = amount0;
        emit HardCapAmountUpdated(amount0, HARD_CAP_AMOUNT1);
    }

    function setHardCapAmount1(uint256 amount1)
        external
        whenNotEnded
        onlyAdmin
    {
        require(amount1 > MINUM_AMOUNT1, 'INVALID_AMOUNT1');
        HARD_CAP_AMOUNT1 = amount1;
        emit HardCapAmountUpdated(HARD_CAP_AMOUNT0, amount1);
    }

    function setEndBlock(uint256 endBlock) 
        external 
        whenNotEnded 
        onlyAdmin 
    {
        require(endBlock > block.number, 'INVALID_END_BLOCK');
        END_BLOCK = endBlock;
        emit EndBlockUpdated(endBlock);
    }

    function getUserInfo(address user) 
        external 
        view 
        returns (uint256 amount0, uint256 amount1)  
    {
        UserInfo memory userInfo = _userInfos[user];
        amount0 = userInfo.amount0;
        amount1 = userInfo.amount1;
    }

    function getTotalLiquidity() 
        public 
        view 
        returns (uint256 totalLiquidity) 
    {
        if (totalAmount0 == 0 || totalAmount1 == 0) return 0;
        totalLiquidity = Math.sqrt(totalAmount0.mul(totalAmount1));
    }

    function getExactLiquidity(address user) 
        public 
        view 
        returns (uint256 exactLiquidity) 
    {
        if (totalAmount0 == 0 || totalAmount1 == 0) return 0;
        UserInfo memory userInfo = _userInfos[user];
        uint256 _amount0 = userInfo.amount0;
        uint256 _amount1 = userInfo.amount1;
        uint256 exactAmount0 = 
            _amount0.mul(totalAmount1).add(_amount1.mul(totalAmount0)) / totalAmount1.mul(2);
        uint256 exactAmount1 = 
            _amount1.mul(totalAmount0).add(_amount0.mul(totalAmount1)) / totalAmount0.mul(2);
        uint256 calculatedLiquidity = Math.sqrt(exactAmount0.mul(exactAmount1));
        uint256 totalLiquidity = getTotalLiquidity();
        exactLiquidity = 
            calculatedLiquidity.mul(totalLiquidity.sub(MINIMUM_LIQUIDITY)) / totalLiquidity;
    }

    function getLiquidityBalance()
        external
        view
        returns (uint256 balance)
    {
        address pair = Helper.pairFor(factory, token0, token1);
        if (pair == address(0)) return 0;
        balance = IERC20(pair).balanceOf(address(this));
    }

    function addProvision(
        address tokenA,
        address tokenB,
        uint256 amountA,
        uint256 amountB
    ) external whenNotEnded nonReentrant {
        require(checkProviderLimit(msg.sender), 'CheckLimitFailed');

        (address _token0, address _token1) = Helper.sortTokens(tokenA, tokenB);
        require(_token0 == token0 && _token1 == token1, 'INVALID_TOKEN');
        uint256 _amount0 = _token0 == tokenA ? amountA : amountB;
        uint256 _amount1 = _token0 == tokenA ? amountB : amountA;
        require(_amount0 > 0 || _amount1 > 0, 'INVALID_ZERO_AMOUNT');
        UserInfo storage userInfo = _userInfos[msg.sender];
        if (_amount0 > 0) {
            require(totalAmount0 < HARD_CAP_AMOUNT0, 'AMOUNT0_CAPPED');
            uint256 remainingAmount0 = HARD_CAP_AMOUNT0.sub(totalAmount0);
            _amount0 = _amount0 < remainingAmount0 ? _amount0 : remainingAmount0;
            totalAmount0 = totalAmount0.add(_amount0);
            userInfo.amount0 = userInfo.amount0.add(_amount0);
            Helper.safeTransferFrom(
                _token0,
                msg.sender,
                address(this),
                _amount0
            );
        }
        if (_amount1 > 0) {
            require(totalAmount1 < HARD_CAP_AMOUNT1, 'AMOUNT1_CAPPED');
            uint256 remainingAmount1 = HARD_CAP_AMOUNT1.sub(totalAmount1);
            _amount1 = _amount1 < remainingAmount1 ? _amount1 : remainingAmount1;
            totalAmount1 = totalAmount1.add(_amount1);
            userInfo.amount1 = userInfo.amount1.add(_amount1);
            Helper.safeTransferFrom(
                _token1,
                msg.sender,
                address(this),
                _amount1
            );
        }

        emit Provided(msg.sender, _amount0, _amount1);
    }

    function mintLiquidity() 
        external
        whenEndedAndCapped
        nonReentrant
        onlyAdmin
    {
        require(
            IFactory(factory).getPair(token0, token1) == address(0), 
            'PAIR_EXISTS'
        );
        IFactory(factory).createPair(token0, token1);
        address pair = Helper.pairFor(factory, token0, token1);
        Helper.safeTransfer(token0, pair, totalAmount0);
        Helper.safeTransfer(token1, pair, totalAmount1);
        IPair(pair).mint(address(this));
    }

    function claim() 
        external 
        whenEndedAndCapped 
        whenLiquidityMinted
        nonReentrant
    {
        UserInfo storage userInfo = _userInfos[msg.sender];
        require(
            userInfo.amount0 > 0 || userInfo.amount1 > 0, 
            'INSUFFICIENT_AMOUNT'
        );
        uint256 exactLiquidity = getExactLiquidity(msg.sender);
        require(exactLiquidity > 0, 'INSUFFICIENT_LIQUIDITY');
        userInfo.amount0 = 0;
        userInfo.amount1 = 0;
        address pair = Helper.pairFor(factory, token0, token1);
        Helper.safeTransfer(pair, msg.sender, exactLiquidity);

        if (rewardTokens.length > 0){
            distributeReward(msg.sender, exactLiquidity, getTotalLiquidity());
        }

        emit LiquidityClaimed(msg.sender, exactLiquidity);
    }

    function refund()
        external
        whenEndedAndFailed
        nonReentrant
    {
        UserInfo storage userInfo = _userInfos[msg.sender];
        require(
            userInfo.amount0 > 0 || userInfo.amount1 > 0, 
            'INSUFFICIENT_AMOUNT'
        );
        uint256 _amount0 = userInfo.amount0;
        uint256 _amount1 = userInfo.amount1;
        if (_amount0 > 0) {
            totalAmount0 = totalAmount0.sub(_amount0);
            userInfo.amount0 = 0;
            Helper.safeTransfer(token0, msg.sender, _amount0);
        }
        if (_amount1 > 0) {
            totalAmount1 = totalAmount1.sub(_amount1);
            userInfo.amount1 = 0;
            Helper.safeTransfer(token1, msg.sender, _amount1);
        }

        emit Refund(msg.sender, _amount0, _amount1);
    }

    /**
     * @dev Return funds directly transfered to this contract, will not affect the portion of the amount 
     *      that participated in bootstrap using `addProvision` function
     **/
    function withdrawExtraFunds(
        address token,
        address to, 
        uint256 amount
    ) external onlyAdmin {
        if (token == token0) {
            uint256 token0Balance = IERC20(token0).balanceOf(address(this));
            require(token0Balance.sub(amount) >= totalAmount0, 'INSUFFICIENT_TOKEN_BALANCE');
        }
        if (token == token1) {
            uint256 token1Balance = IERC20(token1).balanceOf(address(this));
            require(token1Balance.sub(amount) >= totalAmount1, 'INSUFFICIENT_TOKEN_BALANCE');
        }
        Helper.safeTransfer(token, to, amount);

        emit WithdrawExtraFunds(token, to, amount);
    }

    function setRewardAndLimit(        
        address[] memory _rewardTokens,
        address[] memory _limitTokens,
        uint256[] memory _limitAmounts
    ) external onlyAdmin{
        rewardTokens = _rewardTokens;
        limitTokens  = _limitTokens;
        limitTokenAmounts = _limitAmounts;

        emit SetRewardAndLimit(_rewardTokens, _limitTokens, _limitAmounts);
    }

    function charge(
        uint256[] memory _amounts
    ) external onlyAdmin {
        require(_amounts.length == rewardTokens.length, 'INVALID_AMOUNTS');
        for (uint256 i = 0; i < _amounts.length; i++) {
            if ( _amounts[i] > 0 ){
                 Helper.safeTransferFrom(
                    rewardTokens[i], 
                    msg.sender, 
                    address(this), 
                    _amounts[i]
                );
            }
        }

        if (rewardTokenAmounts.length == 0){
           rewardTokenAmounts = _amounts;     
        }else{
            for(uint256 i = 0; i < _amounts.length; i++){
                rewardTokenAmounts[i] = rewardTokenAmounts[i].add(_amounts[i]);
            }
        }

        emit ChargeReward(msg.sender, rewardTokens, _amounts);
    }

    function withdrawReward(address recipient) external onlyAdmin{
        for (uint256 i = 0; i < rewardTokens.length; i++) {
            if (rewardTokenAmounts[i] == 0){
                continue;
            }
            Helper.safeTransfer(
                rewardTokens[i],  
                recipient, 
                rewardTokenAmounts[i]
            );
            rewardTokenAmounts[i] = 0;
        }
    }

    function checkProviderLimit(address provider) public view returns(bool success){
        success = true;
        for (uint256 i = 0; i < limitTokenAmounts.length; i++){
            uint256 balance =  IERC20(limitTokens[i]).balanceOf(provider);

            if (balance < limitTokenAmounts[i]){
                success = false;
                break;
            }
        }
    }

    function distributeReward(address provider, uint256 providerLiquidity, uint256 totalLiquidity) private{
        uint256[] memory rewardAmounts = new uint256[](rewardTokens.length);

        for (uint256 i = 0; i < rewardTokens.length; i++){
            uint256 distributeRewardAmount = providerLiquidity.mul(rewardTokenAmounts[i]) / totalLiquidity;
            if (distributeRewardAmount > 0) {
                Helper.safeTransfer(
                    rewardTokens[i],
                    provider, 
                    distributeRewardAmount
                );
            }
            rewardAmounts[i] = distributeRewardAmount;
        }

        emit DistributeReward(provider, rewardTokens, rewardAmounts);
    }

    function getRewardTokens() external view returns(address[] memory tokens){
        tokens = rewardTokens;
    }

    function getLimitTokens() external view returns(address[] memory tokens){
        tokens = limitTokens;
    }

    function getLimitAmounts() external view returns(uint256[] memory amounts){
        amounts = limitTokenAmounts;
    }

    function getRewardTokenAmounts() external view returns(uint256[] memory amounts){
        amounts = rewardTokenAmounts;
    }

    function estimateRewardTokenAmounts(address who) external view returns(uint256[] memory amounts){
        uint256 whoLiquidity = getExactLiquidity(who);
        uint256 totalLiquidity = getTotalLiquidity();
        amounts = new uint256[](rewardTokens.length);

        for (uint256 i = 0; i < rewardTokens.length; i++){
            amounts[i] = whoLiquidity.mul(rewardTokenAmounts[i]) / totalLiquidity;
        }
    }
}

contracts/periphery/Migrator.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IMigrator } from "./interfaces/IMigrator.sol";
import { IRouter } from "./interfaces/IRouter.sol";
import { IFactory } from "../core/interfaces/IFactory.sol";
import { IPair } from "../core/interfaces/IPair.sol";
import { IWNativeCurrency } from "./interfaces/IWNativeCurrency.sol";
import { Helper } from "../libraries/Helper.sol";

contract Migrator is IMigrator {
    using SafeERC20 for IERC20;

    IFactory immutable factoryV1;
    IRouter immutable router;
    address immutable wnative;

    error InvalidMinAmountsParams();

    constructor(IFactory _factoryV1, IRouter _router, address _wnative) {
        factoryV1 = _factoryV1;
        router = _router;
        wnative = _wnative;
    }

    receive() external payable {
        require(msg.sender == wnative);
    }

    function _migrate(IPair pair, uint256 amount0Min, uint256 amount1Min, address to, uint256 deadline) internal {
        {
            uint256 liquidity = IERC20(address(pair)).balanceOf(msg.sender);
            IERC20(address(pair)).safeTransferFrom(msg.sender, address(pair), liquidity);
        }
        address token0 = pair.token0();
        address token1 = pair.token1();
        (uint256 amount0V1, uint256 amount1V1) = pair.burn(address(this));
        IERC20(token0).safeApprove(address(router), amount0V1);
        IERC20(token1).safeApprove(address(router), amount1V1);
        (uint256 amount0V2, uint256 amount1V2, ) = router.addLiquidity(
            token0, 
            token1, 
            amount0V1, 
            amount1V1, 
            amount0Min, 
            amount1Min, 
            to, 
            deadline
        );
        if (amount0V1 > amount0V2) {
            IERC20(token0).safeApprove(address(router), 0);
            _transferBack(token0, amount0V1 - amount0V2, to);
        } else if (amount1V1 > amount1V2) {
            IERC20(token1).safeApprove(address(router), 0);
            _transferBack(token1, amount1V1 - amount1V2, to);
        }
    }

    function _transferBack(address token, uint256 amount, address to) internal {
        if (token == wnative) {
            IWNativeCurrency(wnative).withdraw(amount);
            Helper.safeTransferNativeCurrency(to, amount);
        } else {
            IERC20(token).safeTransfer(to, amount);
        }
    }

    function migrate(
        IPair pair, 
        uint256 amount0Min, 
        uint256 amount1Min, 
        address to, 
        uint256 deadline
    ) override external {
        _migrate(pair, amount0Min, amount1Min, to, deadline);
    }

    function migrateMany(
        IPair[] memory pairs, 
        uint256[] memory amounts0Min, 
        uint256[] memory amounts1Min, 
        address to, 
        uint256 deadline
    ) override external {
        uint256 commonLength = pairs.length;
        if (amounts0Min.length != commonLength || amounts1Min.length != commonLength) 
            revert InvalidMinAmountsParams();
        for (uint256 i; i < commonLength; i++) {
            _migrate(pairs[i], amounts0Min[i], amounts1Min[i], to, deadline);
        }
    }
}

